mapper.select:

SELECT MEM_EMAIL
FROM MEMBER
WHERE MEM_EMAIL = #{MEM_EMAIL}


			SELECT COUNT(*)
			FROM MEMBER
			WHERE MEM_NAME = #{MEM_NAME} 
            	AND MEM_PHONE = #{MEM_PHONE}
            	AND MEM_DEL_GB = 'Y'


			SELECT COUNT(MEM_DEL_TIME)
			FROM MEMBER
			WHERE SYSDATE - MEM_DEL_TIME >= 7
			AND MEM_NAME = #{MEM_NAME} 
            	AND MEM_PHONE = #{MEM_PHONE}
