mapper.select:
SELECT * FROM (SELECT
BRAND_NUM, BRAND_NAME, TOTAL_GOODS_NUM, TOTAL_GOODS_LIKE_COUNT, TOTAL_GOODS_SCORE_AVG, TOTAL_GOODS_NAME, TOTAL_GOODS_MODEL, TOTAL_GOODS_CATEGORY, TOTAL_GOODS_PRICE, GOODS_IMAGE_STD FROM (SELECT DISTINCT B.BRAND_NUM, B.BRAND_NAME, TG.TOTAL_GOODS_NUM, TG.TOTAL_GOODS_LIKE_COUNT, TG.TOTAL_GOODS_SCORE_AVG, TG.TOTAL_GOODS_NAME, TG.TOTAL_GOODS_MODEL, TG.TOTAL_GOODS_CATEGORY, TG.TOTAL_GOODS_PRICE, GI.GOODS_IMAGE_STD FROM BRAND B, TOTAL_GOODS TG, GOODS_DETAIL GD, GOODS_IMAGE GI, GOODS_LIKE GL WHERE B.BRAND_NUM = TG.TOTAL_GOODS_BRAND AND TG.TOTAL_GOODS_NUM = GD.GOODS_DETAIL_NUM AND TG.TOTAL_GOODS_NUM = GI.GOODS_IMAGE_PARENT AND TG.TOTAL_GOODS_NUM = GL.GOODS_LIKE_PRONUM AND TG.TOTAL_GOODS_DEL_GB = 'N' AND GI.GOODS_IMAGE_ORDER = 1
) T) WHERE NUM BETWEEN #{START} AND #{END}



SELECT COUNT(*)
FROM GOODS_LIKE
WHERE GOODS_LIKE_USER = #{MEM_NUM}
AND GOODS_LIKE_PRONUM = #{TOTAL_GOODS_NUM}

mapper.insert:

INSERT INTO GOODS_LIKE(
		          GOODS_LIKE_NUM,
		          GOODS_LIKE_USER,
		          GOODS_LIKE_PRONUM
		          )
VALUES (
	GOODS_LIKE_SEQ.NEXTVAL,
	#{MEM_NUM},
	#{GOODS_LIKE_PRONUM}
	)

mapper.delete:

DELETE FROM GOODS_LIKE
WHERE GOODS_LIKE_PRONUM = #{GOODS_LIKE_PRONUM}
      AND GOODS_LIKE_USER = #{MEM_NUM}

mapper.update:

UPDATE TOTAL_GOODS
SET TOTAL_GOODS_LIKE_COUNT = TOTAL_GOODS_LIKE_COUNT + 1
WHERE TOTAL_GOODS_NUM = #{GOODS_LIKE_PRONUM}


UPDATE TOTAL_GOODS
SET TOTAL_GOODS_LIKE_COUNT = TOTAL_GOODS_LIKE_COUNT - 1
WHERE TOTAL_GOODS_NUM = #{GOODS_LIKE_PRONUM}
